      it "assigns a newly created but unsaved menuset as @menuset" do
        Menuset.stub(:new).with({'these' => 'params'}) { mock_menuset(:save => false) }
        post :create, :menuset => {'these' => 'params'}
        assigns(:menuset).should be(mock_menuset)
      end

      it "re-renders the 'new' template" do
        Menuset.stub(:new) { mock_menuset(:save => false) }
        post :create, :menuset => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested menuset" do
        Menuset.stub(:find).with("37") { mock_menuset }
        mock_menuset.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => "37", :menuset => {'these' => 'params'}
      end

      it "assigns the requested menuset as @menuset" do
        Menuset.stub(:find) { mock_menuset(:update_attributes => true) }
