      it "assigns the menuset as @menuset" do
        Menuset.stub(:find) { mock_menuset(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:menuset).should be(mock_menuset)
      end

      it "re-renders the 'edit' template" do
        Menuset.stub(:find) { mock_menuset(:update_attributes => false) }
        put :update, :id => "1"
        response.should render_template("edit")
      end
    end
  end

